import React from "react";
import Navbar from "../../components/Navbar/Navbar";
import "./CardDetailPage.css";

import { useLocation } from "react-router-dom";

function CardDetailPage(props) {
  const location = useLocation();
  const { movieContent } = location.state;

  let author = movieContent.author;

  let authorImgUrl =
    "https://media-exp1.licdn.com/dms/image/C4E03AQFYIhvlCLQ7fw/profile-displayphoto-shrink_800_800/0/1634408897588?e=1655337600&v=beta&t=9eu3H4crWM93UrbCh2daNc4tQywK2ZnUk4cfewU_Q8s";

  return (
    <div className="page-container">
      <Navbar home />
      <div className="author-info">
        <img className="author-img" src={author.imageUrl} alt="cardImage" />
        <div className="author-text">
          <div className="author-name">{author.name}</div>
          <div className="author-date">{author.date}</div>
        </div>
      </div>
      <div className="movie-container">
        <div className="movie-header">
          <div className="movie-img">
            <img className="card-img" src={movieContent.imageUrl} alt="cardImage"/>
            <div className="card-name">{movieContent.name}</div>
          </div>
          <div className="movie-title">{movieContent.title}</div>
        </div>
        <div className="movie-content">{movieContent.text}</div>
      </div>
    </div>
  );
}

export default CardDetailPage;
