import React, { useState, useEffect } from "react";
import "./Home.css";
import Navbar from "../../components/Navbar/Navbar";
import Footer from "../../components/Footer/Footer";
import MyButton from "../../components/Buttons/MyButton";
import { Formik } from "formik";
import axios from "axios";
import FormInput from "../../components/FormInput/FormInput";
import { Link } from "react-router-dom";
import { ChevronDown } from "react-feather";
import BlogCard from "../../components/BlogCard/BlogCard";

import data from '../../data/myExampleData.json';

function HomePage() {
  const [showAddBlog, setAddBlog] = useState(false);
  const [showListBlog, setShowListBlog] = useState(false);
  const [post, setPost] = React.useState(null);

  const baseURL = "https://jsonplaceholder.typicode.com/users/1";

  useEffect(() => {
    axios.get(`${baseURL}`).then((response) => {
      setPost(response.data);
    });
  }, []);

  return (
    <div className="home-page">
      <Navbar addBlog login sign social />
      <div className="content-container">
        {/* Content Header -- BEGIN */}
        <div className="content-header">
          <div className="header-item">Film çok mu hoşuna gitti ?</div>
          <div className="header-item">
            Duygularını paylaşmak mı istiyorsun ?
          </div>
          <div className="header-item">
            Moviegram ile hemen{" "}
            <Link className="header-link" to="/login">
              Blog Oluştur !
            </Link>
          </div>
        </div>
        <div className="show-blogs-container">
          <div className="show-blogs-title">Blogları İncele</div>
          <div className="show-blogs-icon">
            <ChevronDown />
          </div>
        </div>
        {/* Content Header -- END  */}

        <div className="blog-container">
          <div className="show-blog-container">
            <div className="blogs-title">Bloglar</div>
            <div className="blog-list">
              <BlogCard title="Örümcek-Adam: Eve Dönüş Yok" imgUrl="https://m.media-amazon.com/images/M/MV5BMmUwOWVhNmEtZTAxMS00NjhmLTkzM2ItOTI5ODk3NjgyYjk4XkEyXkFqcGdeQXVyNDQ0MTYzMDA@._V1_FMjpg_UX1000_.jpg" text="Spider-Man'in kimliğinin ortaya çıkmasıyla Peter, Doctor Strange'den yardım ister. Bir büyü ters gittiğinde, diğer dünyalardan tehlikeli düşmanlar ortaya çıkmaya başlar ve Peter'ı Örümcek Adam olmanın gerçekten ne anlama geldiğini keşfetmeye zorlar."/>              
              <BlogCard title="Spider-Man" imgUrl="https://www.w3schools.com/images/w3schools_green.jpg" text="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."/>              
              <BlogCard title="Spider-Man" imgUrl="https://www.w3schools.com/images/w3schools_green.jpg" text="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum."/>              
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default HomePage;
