import React, { useState } from "react";
import "./Home.css";
import Navbar from "../../components/Navbar/Navbar";
import Footer from "../../components/Footer/Footer";
import MyButton from "../../components/Buttons/MyButon";
import { Formik } from "formik";
import FormInput from "../../components/FormInput/FormInput";

function HomePage() {
  const [addBlog, setAddBlog] = useState(false);



  return (
    <div className="home-page">
      <Navbar home login sign social />
      <div className="content-container">
        <MyButton
          handleAction={() => {
            setAddBlog(!addBlog);
          }}
          text="Blog Ekle"
        />
        <MyButton text="Blogları Görüntüle" />
      </div>
      <div>
      {
        addBlog &&
        <AddBlog/>
      }

      </div>

      <Footer />
    </div>
  );
}

export function AddBlog (){
  return (
    <div>
      <div><Formik
          className="formik-container"
          initialValues={{ email: "", username: "", password: "" }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
            /* and other goodies */
          }) => (
            <form className="input-container" onSubmit={handleSubmit}>
            <div className="sign-title-container">
                <div className="sign-title">Moviegram'a giriş yap</div>
                <div className="sign-login-title">
                  <div className="gray-title">Moviegram hesabın yok mu ? </div>
                  <Link className="blue-title" to="/sign">
                    Hesap oluştur
                  </Link>
                </div>
              </div>
              <FormInput
                title="Kullanıcı Adı & Mail"
                type="username"
                name="username"
                handleBlur={handleBlur}
                value={values.username}
                onChange={handleChange}
              />
              <FormInput
                title="Parola"
                type="password"
                name="password"
                value={values.password}
                onChange={handleChange}
              />
              <button
                  className="btn-form"
                  type="submit"
                  disabled={isSubmitting}
                >
                  Giriş Yap
                </button>
            </form>
          )}
        </Formik></div>
    </div>
  );
};

export default HomePage;
