import React, {useState} from "react";
import "./Home.css";
import Navbar from "../../components/Navbar/Navbar";
import Footer from "../../components/Footer/Footer";
import MyButton from "../../components/Buttons/MyButon";
import { Formik } from "formik";
import FormInput from "../../components/FormInput/FormInput";
import { render } from "@testing-library/react";

function HomePage() {

  const [addBlog, setAddBlog] = useState(false);

  const _renderAddBlog = () => {
    render(
      <div>
        <div>Blog Ekle</div>
        <div>
          <div>
            <Formik
              className="formik-container"
              initialValues={{ email: "", username: "", password: "" }}
              onSubmit={(values, { setSubmitting }) => {
                setTimeout(() => {
                  alert(JSON.stringify(values, null, 2));
                  setSubmitting(false);
                }, 400);
              }}
            >
              {({
                values,
                errors,
                touched,
                handleChange,
                handleBlur,
                handleSubmit,
                isSubmitting,
                /* and other goodies */
              }) => (
                <form className="input-container" onSubmit={handleSubmit}>
                  <div className="sign-title-container">
                    <div className="sign-title">Blog Ekle</div>
                  </div>
                  <FormInput
                    title="Kullanıcı Adı & Mail"
                    type="username"
                    name="username"
                    handleBlur={handleBlur}
                    value={values.username}
                    onChange={handleChange}
                  />
                  <FormInput
                    title="Parola"
                    type="password"
                    name="password"
                    value={values.password}
                    onChange={handleChange}
                  />
                  <button
                    className="btn-form"
                    type="submit"
                    disabled={isSubmitting}
                  >
                    Giriş Yap
                  </button>
                </form>
              )}
            </Formik>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="home-page">
      <Navbar home login sign social />
      <div className="content-container">
        <MyButton text="Blog Ekle" />
        <MyButton h text="Blogları Görüntüle" />
      </div>
        {
          addBlog &&
            _renderAddBlog
        }
      <Footer />
    </div>
  );
}

export default HomePage;
