import React from 'react'
import { Link } from "react-router-dom";
import './BlogCard.css'

function BlogCard(props){


    let cardContent= {
        cardImageUrl : props.imgUrl,
        cardTitle : props.title,
        cardText : props.text
    }

    let linkTo ={
        pathName:"cardDetail",
        movieContent:"cardContent"
    }

    return(
        <Link className='card-container' to={linkTo}>
            <img className='card-img'  src={cardContent.cardImageUrl} alt="cardImage"/>
            <div className='card-title'>{cardContent.cardTitle}</div>
            <div className='card-text'>{cardContent.cardText}</div>
        </Link>
    )
}

export default BlogCard;
