import React, { useState, useEffect } from "react";
import "./Home.css";
import Navbar from "../../components/Navbar/Navbar";
import Footer from "../../components/Footer/Footer";
import MyButton from "../../components/Buttons/MyButton";
import { Formik } from "formik";
import axios from "axios";
import FormInput from "../../components/FormInput/FormInput";

function HomePage() {
  const [showAddBlog, setAddBlog] = useState(false);
  const [showListBlog, setShowListBlog] = useState(false);
  const [post, setPost] = React.useState(null);

  const baseURL = "https://jsonplaceholder.typicode.com/users/1";

  useEffect(() => {
    axios.get(`${baseURL}`).then((response) => {
      setPost(response.data);
    });
  }, []);

  return (
    <div className="home-page">
      <Navbar home login sign social />
      <div className="content-container">
        <div className="action-buttons">
          <MyButton
            handleAction={() => {
              setShowListBlog(false);
              setAddBlog(!showAddBlog);
            }}
            text="Blog Ekle"
          />
          <MyButton
            colored
            text="Blogları Görüntüle"
            handleAction={() => {
              setAddBlog(false);
              setShowListBlog(!showListBlog);
            }}
          />
          <div></div>
        </div>

        <div>
          {showAddBlog && <AddBlog />}
          {showListBlog && <ShowBlogs post={post} />}
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default HomePage;


export function ShowBlogs(props) {
  let { post } = props;

  return (
    <div className="show-blog-container">
      <div className="blogs-title">Bloglar</div>
      <div>
        <div className="result-container">
          <div className="result-title">Post Result :</div>
          <div className="result-post">{post && post.name}</div>
        </div>
      </div>
    </div>
  );
}


export function AddBlog() {
  return (
    <div>
      <div>
        <Formik
          className="formik-container"
          initialValues={{ title: "", text: "" }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
            /* and other goodies */
          }) => (
            <form className="input-container" onSubmit={handleSubmit}>
              <div className="sign-title-container">
                <div className="sign-title">Blog ekle</div>
              </div>
              <FormInput
                title="Blog Başlığı"
                type="title"
                name="title"
                handleBlur={handleBlur}
                value={values.title}
                onChange={handleChange}
              />
              <FormInput
                title="Blog Metni"
                type="text"
                name="text"
                value={values.text}
                onChange={handleChange}
              />      
              <button
                className="btn-form"
                type="submit"
                disabled={isSubmitting}
              >
                Ekle
              </button>
            </form>
          )}
        </Formik>
      </div>
    </div>
  );
}
