import React from 'react'
import { Link } from "react-router-dom";
import './BlogCard.css'

function BlogCard(props){

    let cardContent = props.movieData;

    return(
        <Link className='card-container' to="cardDetail" state={{ movieContent: cardContent }}>
            <img className='card-img'  src={cardContent.cardImgUrl} alt="cardImage"/>
            <div className='card-title'>{cardContent.cardTitle}</div>
            <div className='card-text'>{cardContent.cardText}</div>
        </Link>
    )
}

export default BlogCard;
